describe('Shopping Cart Feature', () => {
  const userId = 'user123';
  const productId = 'product123';

  beforeEach(() => {
    // Clear the cart before each test
    cy.request('DELETE', `/api/cart/${userId}`);

    // Log in via API to ensure user is authenticated before each test
    cy.login('user@shop.com', 'user123');
  });

  it('should display the cart count correctly after adding an item', () => {
    // Intercept the GET request to load the cart
    cy.intercept('GET', `/api/cart/${userId}`, { fixture: 'cart/empty.json' }).as('getCart');
    // Intercept the POST request to add an item to the cart
    cy.intercept('POST', `/api/cart/${userId}`, { statusCode: 200 }).as('addItemToCart');

    cy.visit('/');

    // Ensure the cart is initially empty
    cy.wait('@getCart');
    cy.get('[data-testid="cart-count"]').should('have.text', '0');

    // Add an item to the cart via UI action
    cy.get('[data-testid="nav-products"]').click();
    cy.get(`[data-testid="add-to-cart-${productId}"]`).click();

    // Ensure the POST request was successful
    cy.wait('@addItemToCart');

    // Check the cart count is updated
    cy.get('[data-testid="cart-count"]').should('have.text', '1');
  });

  it('should display an error when trying to add an item without a valid session', () => {
    // Intercept POST request and simulate unauthorized response
    cy.intercept('POST', `/api/cart/${userId}`, { statusCode: 401, body: { error: 'Unauthorized' } }).as('addItemUnauthorized');

    cy.visit('/');

    // Log out to simulate invalid session
    cy.get('[data-testid="nav-logout"]').click();

    // Attempt to add an item which should fail
    cy.get('[data-testid="nav-products"]').click();
    cy.get(`[data-testid="add-to-cart-${productId}"]`).click();

    // Wait for the intercepted unauthorized response
    cy.wait('@addItemUnauthorized');

    // Check that the cart count has not increased
    cy.get('[data-testid="cart-count"]').should('have.text', '0');
  });

  it('should remove an item from the cart correctly', () => {
    // Intercept GET to load the cart and simulate a cart with one item
    cy.intercept('GET', `/api/cart/${userId}`, { fixture: 'cart/one-item.json' }).as('getCartWithItem');
    // Intercept DELETE request to remove the item
    cy.intercept('DELETE', `/api/cart/${userId}/${productId}`, { statusCode: 200 }).as('removeItemFromCart');

    cy.visit('/');

    // Ensure the cart has one item
    cy.wait('@getCartWithItem');
    cy.get('[data-testid="cart-count"]').should('have.text', '1');

    // Remove the item from the cart via UI action
    cy.get('[data-testid="nav-cart"]').click();
    cy.get(`[data-testid="remove-from-cart-${productId}"]`).click();

    // Ensure the DELETE request was successful
    cy.wait('@removeItemFromCart');

    // Check the cart count is updated
    cy.get('[data-testid="cart-count"]').should('have.text', '0');
  });

  it('should handle a failed login attempt', () => {
    // Intercept login attempt and simulate failure
    cy.intercept('POST', '/api/login', { statusCode: 401, body: { error: 'Invalid credentials' } }).as('failedLogin');

    cy.visit('/');

    // Attempt login with incorrect credentials
    cy.get('[data-testid="nav-login"]').click();
    cy.get('[data-testid="email-input"]').type('wrong@shop.com');
    cy.get('[data-testid="password-input"]').type('wrongpassword');
    cy.get('[data-testid="login-button"]').click();

    // Wait for the intercepted login failure
    cy.wait('@failedLogin');

    // Check for error message visibility
    cy.get('[data-testid="login-error"]').should('be.visible').and('contain', 'Invalid credentials');
  });
});

